INSERT INTO `wp_posts` VALUES(289, 1, '2008-11-26 15:13:26', '2008-11-26 19:13:26', '[caption id="attachment_38" align="aligncenter" width="500" caption="The Riveters, 2008"]<a href="http://www.bigwillydier.com/launch/wp-content/uploads/2008/11/riveters08.jpg"><img class="size-full wp-image-38" title="The Riveters, 2008" src="http://www.bigwillydier.com/launch/wp-content/uploads/2008/11/riveters08.jpg" alt="The Riveters, 2008" width="500" height="600" /></a>[/caption]\r\n<p style="text-align: center;"><strong>MEET THE 2008 RIVETERS</strong></p>\r\n\r\n<p style="text-align: center;"><strong>Captain</strong> - Sass E. McNasty\r\nBaby Fighterfly\r\nBurnin'' Helen\r\nCraisy Dukes\r\nDaSilva Bullet\r\nElle Razor\r\nFo'' Shizzy Borden\r\nHysterica!\r\nJetta Von Diesel\r\nKid Ace\r\nLotta Pain\r\nShelby Bruisin''\r\nRhode Kill\r\nTrophy Knife\r\n\r\n</p><p style="text-align: center;"><strong>Alternates:</strong>\r\nMalady D''Amour\r\nViolent Vixxen\r\n\r\n</p><p style="text-align: center;"><strong>Manager:</strong>\r\nWicked Stella\r\n\r\n</p><p style="text-align: center;"><strong>Lost but not forgotten:</strong>\r\nBunnicula\r\nCleo Patronize\r\nHot Sauce\r\nJailbird Jenny\r\nJean Harlot\r\n</p><p style="text-align: center;">The Rhode Island Riveters are Providence Roller Derby''s All-Star Travel Team. Composed of the best in the league, they compete nationally in individual bouts as well as multi-bout tournaments. The Riveters consistently rank in the top 20 leagues in the Nation (out of the approximately 55 WFTDA leagues). Inspired by Rosie the Riveter''s work ethic, these ladies practice hard, skate hard, and plan on bringing home win after win for the Ocean State! We Can Do It!</p>', 'The Riveters', 0, '', 'inherit', 'open', 'open', '', '181-revision-20', '', '', '2008-11-26 15:13:26', '2008-11-26 19:13:26', '', 181, 'http://www.bigwillydier.com/launch/2008/11/181-revision-20/', 0, 'revision', '', 0);
