INSERT INTO `wp_posts` VALUES(253, 1, '2008-11-26 14:27:29', '2008-11-26 18:27:29', 'PRD''S STRIPE BRIGADE\r\nNo one would be able to follow roller derby if it weren''t for our referees. They keep points, track penalties, and eject those extra-saucy ladies for putting too much smack down. Some of our refs are skaters from men''s and co-ed roller derby leagues, coaches at league practices, and sometimes a retired or guest skater will pop by to throw on some stripes and lay down the law of the rink.', 'Referees', 0, '', 'inherit', 'open', 'open', '', '134-revision-3', '', '', '2008-11-26 14:27:29', '2008-11-26 18:27:29', '', 134, 'http://www.bigwillydier.com/launch/2008/11/134-revision-3/', 0, 'revision', '', 0);
INSERT INTO `wp_posts` VALUES(254, 1, '2008-12-05 11:53:17', '2008-12-05 15:53:17', '[caption id="attachment_360" align="aligncenter" width="500" caption="L2R: Reffington Steele, Adamn Good Call, Fresh Eddie Fresh, Big Willy DIE-r, Lions&#39; Jen"]<a href="http://www.bigwillydier.com/launch/wp-content/uploads/2008/12/refs1.jpg"><img class="size-full wp-image-360  " title="The 2008 Refs!" src="http://www.bigwillydier.com/launch/wp-content/uploads/2008/12/refs1.jpg" alt="The 2008 Refs!" width="500" height="333" /></a>[/caption]\n\n<strong>PRD''S STRIPE BRIGADE</strong>\nNo one would be able to follow roller derby if it weren''t for our referees. They keep points, track penalties, and eject those extra-saucy ladies for putting too much smack down. Some of our refs are skaters from men''s and co-ed roller derby leagues, coaches at league practices, and sometimes a retired or guest skater will pop by to throw on some stripes and lay down the law of the rink.\n<p style="text-align: center;"><strong>Our exhalted PRD Referees..</strong></p>\n\n<p style="text-align: center;"><strong>Head Ref -</strong> Crash Daily\nAdamn Good Call\nBig Willy DIE-r\nBubba\nBuzzy O''Ficial\nFresh Eddie Fresh\nLions'' Jen\nMomma Lou Screw\nRefington Steel\nRhode Hazahd\nRhody Round-up</p>', 'Referees', 0, '', 'inherit', 'open', 'open', '', '134-autosave', '', '', '2008-12-05 11:53:17', '2008-12-05 15:53:17', '', 134, 'http://www.bigwillydier.com/launch/2008/11/134-autosave/', 0, 'revision', '', 0);
INSERT INTO `wp_posts` VALUES(255, 1, '2008-11-26 14:27:54', '2008-11-26 18:27:54', '<strong>PRD''S STRIPE BRIGADE</strong>\r\nNo one would be able to follow roller derby if it weren''t for our referees. They keep points, track penalties, and eject those extra-saucy ladies for putting too much smack down. Some of our refs are skaters from men''s and co-ed roller derby leagues, coaches at league practices, and sometimes a retired or guest skater will pop by to throw on some stripes and lay down the law of the rink.', 'Referees', 0, '', 'inherit', 'open', 'open', '', '134-revision-4', '', '', '2008-11-26 14:27:54', '2008-11-26 18:27:54', '', 134, 'http://www.bigwillydier.com/launch/2008/11/134-revision-4/', 0, 'revision', '', 0);
