INSERT INTO `wp_posts` VALUES(313, 1, '2008-11-26 16:08:44', '2008-11-26 20:08:44', 'We would like to wish our fans, sponsors and supporters a  happy holiday season.  The girls are training hard in this off-season and also spending time with family and friends during the holidays. Rest assured, we <strong><em>WILL</em></strong> be  back in full force in January of 2009, making appearances throughout the community and  preparing for the 2009 season!\r\n\r\nWe look forward to seeing you all then!\r\n\r\nFrom all of us here at Providence Roller Derby,\r\n\r\nHAPPY HOLIDAYS! :)', 'Happy Holidays from Providence Roller Derby!! ', 0, '', 'publish', 'open', 'closed', '', 'happy-holidays-from-providence-roller-derby', '', '', '2008-11-26 19:31:04', '2008-11-26 23:31:04', '', 0, 'http://www.bigwillydier.com/launch/?p=313', 0, 'post', '', 0);
