			echo ' &nbsp;</div>' . ($bfa_ata['home_single_next_prev'] != '' ? '<div class="home"><a href="' . get_option('home') . '/">' . 
			$bfa_ata['home_single_next_prev'] . '</a></div>' : '') . '<div class="newer' . ($bfa_ata['home_single_next_prev'] != '' ? '-home' : '') . '">&nbsp; '; 
			$bfa_ata['next_prev_orientation'] == 'Older Left, Newer Right' ? next_post_link($bfa_ata['single_next_prev_newer']) : 
			previous_post_link($bfa_ata['single_next_prev_older']);
			echo '</div>
			<div class="clearboth"></div>
			</div>';			
		} ?>
		
		
		<?php // Post Container
		if ( function_exists('post_class') ) { ?>	
		<div <?php if ( is_page() ) { post_class('post'); } else { post_class(); } ?> id="post-<?php the_ID(); ?>">
		<?php } else { ?>
		<div class="<?php echo ( is_page() ? 'page ' : '' ) . 'post" id="post-'; the_ID(); ?>">
		<?php } ?>
		
		<?php // Post Kicker
		if( (is_home() && $bfa_ata['post_kicker_home'] != "") OR 
