		( (is_archive() OR is_search() OR is_author() OR is_tag()) && $bfa_ata['post_byline_multi'] != "") ) {
			echo '<div class="post-byline">';
			if ( is_home() ) { echo postinfo($bfa_ata['post_byline_home']); } 
			elseif ( is_page() ) { echo postinfo($bfa_ata['post_byline_page']); } 
			elseif ( is_single() ) { echo postinfo($bfa_ata['post_byline_single']); } 
