			wp_link_pages('before=<p class="post-pagination"><strong>' . __('Pages:','atahualpa') . '</strong> &after=</p>&next_or_number=number'); 
		} ?>

						
						
		<?php // Show Archives page if configured
		if ( is_page() AND $current_page_id == $bfa_ata['archives_page_id'] ) { ?>
