				<ul>
				<?php wp_get_archives('type=' . $bfa_ata['archives_date_type'] . '&show_post_count=' . ($bfa_ata['archives_date_count'] == "Yes" ? '1' : '0')); ?>
				</ul>
			<?php } 
							
			if ( $bfa_ata['archives_category_show'] == "Yes" ) { ?>
				<h3><?php echo $bfa_ata['archives_category_title']; ?></h3>
				<ul>
